//     console.log('res: '+ JSON.stringify(api.data));
//   })
// };

// getData();

function App() {
  const query = window.location.search;
  const params = new URLSearchParams(query);
  let number;

  if (params.has('n')){
    number = params.get('n');
  }else{
    number = 1;
  }

  return (
    <>
      <nav>
       Comic Viewer
      </nav>
      <div id="body">
        <Comic number={number}/>
      </div>
    </>
  );
}

const Comic = (props) => { 
  let num = props.number;

  const [Data, setData] = useState();

  const getData = async (req,res) => {
    axios.get('http://localhost:4000/getData', {params:{req}})
    .then(api => {
      setData(JSON.stringify(api.data));
      console.log('res: '+ JSON.stringify(api.data));
    })
  };

  // getData(num);

  const previous = (e) => {
    e.preventDefault();
    if ((num - 1) === 0){
      num  = 1000;
    }else{
      num -= 1;
    }
    console.log(num)
    getData(num);
  };

  const next = (e) => {
    e.preventDefault();
    if ((num + 1) === 1001){
      num  = 1;
    }else{
      num += 1;
    }
    console.log(num)
    getData(num);
  };


  return <>
    <Number number={num}/>
      <div id="comic">
        <span id="previous" onClick={previous}>&lt;-</span>
        <img src="Data" alt="Comic" id="comicImg"/>
        <span id="next" onClick={next}>-&gt;</span>
      </div>
    <Transcript transcript={Data ? Data.transcript : null}/>
  </>

}

const Number = (props) => {
  const num = props.number;
  return <>
    <input type="text" defaultValue={num} id="Number"/>
    <input type="button" value="Search"/>
    <input type="button" value="Random"/>
  </>
}

const Transcript = (props) => {
  const transcript = props.transcript;
  return<>
    <input type="button" value="Transcript"/>
    <p>{transcript}</p>
  </>
}

export default App;
