import React, {useState} from 'react';
import './App.css';
import axios from 'axios';
let [Data, setData] = useState();
const getData = async () => {
  axios.get('http://localhost:4000/getData')
  .then(api => {
    setData = JSON.stringify(api.data);
    console.log('res: '+ JSON.stringify(api.data));
  })
};

getData();

function App() {
  return (
    <>
      <nav>
       Comic Viewer
      </nav>
      <div id="body">
        <input type="text" name="" id="Number"/>
        <input type="button" value="Search"/>
        <input type="button" value="Random"/>
        <div id="comic">
          <span id="previous" onClick={getData}>&lt;-</span>
          <img src={Data.img} alt="Comic" id="comicImg"/>
          <span id="next" >-&gt;</span>
        </div>
        <Transcript/>
      </div>
    </>
  );
}

const Transcript = () => {
  return<>
  <input type="button" value="Transcript"/>
  <p>{Data}</p>
  </>
}

export default App;
