import React, {useState} from 'react';
import './App.css';
import axios from 'axios';

function App() {
  const query = window.location.search;
  const params = new URLSearchParams(query);
  let number;

  if (params.has('n')){
    number = params.get('n');
  }else{
    number = 1;
  }

  return (
    <>
      <nav>
       Comic Viewer
      </nav>
      <div id="body">
        <Comic number={number}/>
      </div>
    </>
  );
}

const Comic = (props) => { 
  const [Num, setNum] = useState(props.number);

  const Data = (req,res) => {
    axios.get('http://localhost:4000/getData', {params:{req}})
    .then(api => {
      return(JSON.stringify(api.data));
    })
  };

  // getData(num);

  const previous = (e) => {
    e.preventDefault();
    if ((Num - 1) < 1){
      Num  = 1000;
    }else{
      Num -= 1;
    }
    console.log(Num)
  };

  const next = (e) => {
    e.preventDefault();
    if ((Num + 1) > 1000){
      Num  = 1;
    }else{
      setN
      Num += 1;
    }
    console.log(Num)
  };


  return <>
    <Number number={Num}/>
      <div id="comic">
        <span id="previous" onClick={previous}>&lt;-</span>
        <img src="Data" alt="Comic" id="comicImg"/>
        <span id="next" onClick={next}>-&gt;</span>
      </div>
    <Transcript transcript={Data ? Data.transcript : null}/>
  </>

}

const Number = (props) => {
  const num = props.number;
  return <>
    <input type="text" defaultValue={num} id="Number"/>
    <input type="button" value="Search"/>
    <input type="button" value="Random"/>
  </>
}

const Transcript = (props) => {
  const transcript = props.transcript;
  return<>
    <input type="button" value="Transcript"/>
    <p>{transcript}</p>
  </>
}

export default App;
