import React, {useState, useEffect} from 'react';
import './App.css';
import axios from 'axios';

function App() {
  const query = window.location.search;
  const params = new URLSearchParams(query);
  let number = 1;

  if (params.has('n')){
    number = params.get('n');
  }else{
    number = 1;
  }

  return (
    <>
      <nav>
       Comic Viewer
      </nav>
      <div id="body">
        <Comic number={number}/>
      </div>
    </>
  );
}

const Comic = (props) => { 
  const [Num, setNum] = useState(props.number);
  const [Data, setData] = useState('');
  var newurl;

  useEffect(() =>{
    const getData = async () => {setData(await axios.get('http://localhost:4000/getData', {params:{Num}}))};
    getData();
  }, [Num]);

  const setNewURL = (num) => {
    newurl = window.location.protocol + "//" + window.location.host + window.location.pathname + '?n=' + num;
    window.history.pushState({path:newurl},'',newurl);
  }

  const previous = (e) => {
    e.preventDefault();
    if ((Num - 1) < 1){
      newurl = window.location.protocol + "//" + window.location.host + window.location.pathname + '?n=' + 1000;
      window.history.pushState({path:newurl},'',newurl);
      setNum(1000);
    }else{
      newurl = window.location.protocol + "//" + window.location.host + window.location.pathname + '?n=' + (parseInt(Num) - 1);
      window.history.pushState({path:newurl},'',newurl);
      setNum(parseInt(Num) - 1);
    }
  };

  const next = (e) => {
    e.preventDefault();
    if ((Num + 1) > 1000){
      newurl = window.location.protocol + "//" + window.location.host + window.location.pathname + '?n=' + 1;
      window.history.pushState({path:newurl},'',newurl);
      setNum(1);
    }else{
      newurl = window.location.protocol + "//" + window.location.host + window.location.pathname + '?n=' + (parseInt(Num) + 1);
      window.history.pushState({path:newurl},'',newurl);
      setNum(parseInt(Num) + 1);
    }
  }; 

  const search = () => {
    const newNum = document.getElementById('number').value;
    newurl = window.location.protocol + "//" + window.location.host + window.location.pathname + '?n=' + newNum;
    window.history.pushState({path:newurl},'',newurl);
    setNewURL
    setNum(newNum);
  }

  const randomNum = () => {
    const newNum = Math.floor(Math.random() * 1001);
    newurl = window.location.protocol + "//" + window.location.host + window.location.pathname + '?n=' + newNum;
    window.history.pushState({path:newurl},'',newurl);
    setNum(newNum);
  }


  return <>
    <input type="text" placeholder={Num} id="number"/>
    <input type="button" value="Search" onClick={search}/>
    <input type="button" value="Random" onClick={randomNum}/>
    {Data.data ? <Info data={Data.data}/>:null}
    <div id="comic">
      <span id="previous" onClick={previous}>&lt;-</span>
      <img src={Data.data ? Data.data.img : '' } alt="Comic" id="comicImg"/>
      <span id="next" onClick={next}>-&gt;</span>
    </div>
    <Transcript transcript={Data.data ? Data.data.transcript : null}/>
  </>
}

const Info = (props) => {
  const data = props.data;
  let date = new Date(2020, data.month - 1, 21);
  let month = date.toLocaleString('en-us', { month: 'long' });

  return <>
    {data 
    ?<><div># {data.num}: {data.title || data.safe_title}</div>Created: {month} {data.day}, {data.year}<div></div></>
    :null}
  </>
}

const Transcript = (props) => {
  const data = props;
  const [isOpen ,setIsOpen] = useState(false);

  const changeOpen = () => {
    setIsOpen(!isOpen)
  }

  return<>
    <input type="button" value="Transcript" onClick={changeOpen}/>
    {isOpen ? <p>{data.transcript || data.alt}</p> : null}
  </>
}

export default App;
