import React, {useState} from 'react';
import './App.css';
import axios from 'axios';

// let [Data, setData] = useState();
let Data, setData;

const getData = async () => {
  const [Data, setData] = useState();
  axios.get('http://localhost:4000/getData')
  .then(api => {
    setData = JSON.stringify(api.data);
    console.log('res: '+ JSON.stringify(api.data));
  })
};

getData();

function App() {
  return (
    <>
      <nav>
       Comic Viewer
      </nav>
      <div id="body">
        <Number/>
        <div id="comic">
          <span id="previous">&lt;-</span>
          <img src="" alt="Comic" id="comicImg"/>
          <span id="next">-&gt;</span>
        </div>
        <Transcript number={Number}/>
      </div>
    </>
  );
}

const Number = () => {
  return <>
    <input type="text" name="" id="Number"/>
    <input type="button" value="Search"/>
    <input type="button" value="Random"/>
  </>
}

const Transcript = (props) => {
  return<>
    <input type="button" value="Transcript"/>
    <p>{Data}</p>
  </>
}

export default App;
