import React, {useState} from 'react';
import './App.css';
import axios from 'axios';
var data;
const getData = async () => {
  axios.get('http://localhost:4000/getData')
  .then(api => {
    data = JSON.stringify(api.data);
    console.log('res: '+ JSON.stringify(api.data));
  })
};

getData();

function App() {
  return (
    <>
      <nav>
       Comic Viewer
      </nav>
      <div id="body">
        <input type="text" name="" id="Number"/>
        <input type="button" value="Random"/>
        <div id="comic">
          <div id="previous">&lt;-</div>
          <img src={data.img} alt="Comic" id="comicImg"/>
          <div id="next">-&gt;</div>
        </div>
        <Transcript/>
        <input type="button" value="Transcript"/>
        <p>{data}</p>
      </div>
    </>
  );
}

export default App;
