import React, {useState, useEffect} from 'react';
import './App.css';
import axios from 'axios';

function App() {
  const query = window.location.search;
  const params = new URLSearchParams(query);
  let number;

  if (params.has('n')){
    number = params.get('n');
  }else{
    number = 1;
  }

  return (
    <>
      <nav>
       Comic Viewer
      </nav>
      <div id="body">
        <Comic number={number}/>
      </div>
    </>
  );
}

const Comic = (props) => { 
  const [Num, setNum] = useState(props.number);
  const [Data, setData] = useState('');

  useEffect(() =>{
    const getData = async () => {setData(await axios.get('http://localhost:4000/getData', {params:{Num}}))};
    getData();
  }, [Num]);

  const previous = (e) => {
    e.preventDefault();
    if ((Num - 1) < 1){
      setNum(1000);
    }else{
      setNum(Num - 1);
    }
  };

  const next = (e) => {
    e.preventDefault();
    if ((Num + 1) > 1000){
      // update query
      setNum(1);
    }else{
      setNum(Num + 1);
    }
  }; 

  return <>
    <Number num={Num}/>
    <div id="comic">
      <span id="previous" onClick={previous}>&lt;-</span>
      <img src={Data.data ? Data.data.img : '' } alt="Comic" id="comicImg"/>
      <span id="next" onClick={next}>-&gt;</span>
    </div>
    <Transcript transcript={Data.data ? Data.data.transcript : null}/>
    <br/>
    <br/>
    <br/>
    <br/>
    <br/>
    <br/>
    <br/>
    {JSON.stringify(Data.data ? Data.data : 'ERROR!')}
  </>

}

const Number = (props) => {
  const num = props.num;
  return <>
    <input type="text" defaultValue={num} id="Number"/>
    <input type="button" value="Search"/>
    <input type="button" value="Random"/>
  </>
}

const Transcript = (props) => {
  const transcript = props.transcript;
  const [isOpen ,setIsOpen] = useState(false);

  const changeOpen = () => {
    setIsOpen(!isOpen)
  }

  useEffect(() => {
    setIsOpen(false)
  },[])

  return<>
    <input type="button" value="Transcript" onClick={changeOpen}/>
    {isOpen ? <p>{transcript}</p> : null}
  </>
}

export default App;
